'use strict'

var util = Util.prototype;

var types = [{ "key": "SENSOR", "value": 1},
			{ "key": "HEALTH", "value": 2},
			{ "key": "ACCELEROMETER", "value": 3}]

function Util() {
}

/* Static Methods */

Util.splitString = function(str, keyword) {
	return str.split(keyword);
};

Util.ktMessageType = function(jsonObj) {
	if (jsonObj.lightLevel != undefined) {
		return types[0].value;
	} else if (jsonObj.batteryLevel != undefined) {
		return types[1].value;
	} else {
		return types[2].value;
	}
}

Util.isKtBeaconSensor = function(value) {
	return value == types[0].value;
}

Util.isKtBeaconHealth = function(value) {
	return value == types[1].value;
}

Util.isKtBeaconAccelerometer = function(value) {
	return value == types[2].value;
}

Util.toJsonOject = function(jsonString) {
	return JSON.parse(jsonString);
}

/* Non-Static Methods */

module.exports = Util;